INSERT INTO `jiudian` VALUES ('4', '酒店地址了', '汉庭民宿', '桂林', '123455', '五星级', '发布一个测试酒店了', '/lvyou/upload/temp/CggYs1bKro-AI0qRAC_IUnLN4pw684_R_300_225.jpg');
INSERT INTO `jiudian` VALUES ('5', '南宁XXXXX', '7天优品', '南宁', '<div class=\"h1_info_head\" style=\"margin:0px;padding:0px;color:#404040;font-family:&quot;font-size:12px;white-space:normal;background-color:#FFFFFF;\">\r\n	<div class=\"hotel-hd clearfix\" style=\"margin:10px 0px;padding:0px;zoom:1;\">\r\n		<span style=\"color:#666666;font-family:&quot;font-size:14px;white-space:normal;background-color:#FFFFFF;\">上海首席公馆酒店是中国首家城市历史文化遗传古典精品酒店，位于上海历史名区新乐路（原法租界）。1932年由法国著名设计师拉法尔设计，为昔日上海滩风云人物黄金荣、杜月笙等人合股公司的办公地所在。整座建筑风格具体地体现了当时拥有社会地位的历史名人气派，同时也蕴涵着丰富的历史文化气息，是一般会所不具备的。酒店里有300余件货真价实的百年历史珍藏品在大厅内展示。&nbsp;</span><span class=\"separator\" style=\"color:#666666;font-family:&quot;font-size:14px;white-space:normal;background-color:#FFFFFF;display:block;width:895px;height:10px;margin:0px;\"></span><span style=\"color:#666666;font-family:&quot;font-size:14px;white-space:normal;background-color:#FFFFFF;\"> 上海首席公馆酒店配有董事会厅、酒会厅、咖啡廊、宴会厅、豪华包房及屋顶花园酒会厅。酒店的管理层皆具有丰富国际酒店经验，为宾客提供一流服务。整座酒店楼底高度达五公尺。酒店内部装修皆经过精细设计，匠心独运，极富怀旧古典气息又不离现代舒适、商务方便的要求，多媒体投影，无线上网，&nbsp;提供餐，洗衣，&nbsp;秘书服务等一应俱全。酒店附近备专用停车位，正门筑有小亭流水，由专人看管，为客人保持高度私隐及更舒适的生活享受。酒店客房均配备豪华按摩冲浪浴缸以及无线上网。酒店为您提供全新层次的热情招待和优质服务，为您呈现无数精彩视角。不管是工作、娱乐、休闲旅游，都为您提供超凡体验。</span><br />\r\n<br />\r\n	</div>\r\n</div>', '三星级', '上海首席酒店', '/lvyou/upload/temp/Cii-T1g-okuINrwwAFBpuAXCcBAAAE_9ABTejIAUGnQ949_w0_h600_c0_t0.JPG');
INSERT INTO `jiudian` VALUES ('6', '桂林市XXXX', '汉庭民宿', '桂林', '12344434', '五星级', '测试酒店信息', '/lvyou/upload/temp/gp1_M00_6D_31_pYYBAFIopvyAII_5AALXHvC1TMA864_300_200.jpg');
