INSERT INTO `xianlu` VALUES ('1', '杭州', '<div class=\"cf\">\r\n	<p style=\"color:#555555;font-family:arial, simsun, sans-serif;font-size:14px;\">\r\n		<img src=\"http://img1.qunarzz.com/vc/3b/6a/80/591a90962d597756ba0907fb81.jpg_92.jpg\" style=\"display:inline;\" alt=\"\" data-lazy=\"http://img1.qunarzz.com/vc/3b/6a/80/591a90962d597756ba0907fb81.jpg_92.jpg\" data-ke-data-lazy=\"http://img1.qunarzz.com/vc/3b/6a/80/591a90962d597756ba0907fb81.jpg_92.jpg\" /><img src=\"http://img1.qunarzz.com/vc/ce/3f/86/aa0610aba24217d6f29552f821.jpg_92.jpg\" style=\"display:inline;\" alt=\"\" data-lazy=\"http://img1.qunarzz.com/vc/ce/3f/86/aa0610aba24217d6f29552f821.jpg_92.jpg\" data-ke-data-lazy=\"http://img1.qunarzz.com/vc/ce/3f/86/aa0610aba24217d6f29552f821.jpg_92.jpg\" /> \r\n	</p>\r\n	<p style=\"color:#555555;font-family:arial, simsun, sans-serif;font-size:14px;\">\r\n		<br />\r\n	</p>\r\n	<p style=\"color:#555555;font-family:arial, simsun, sans-serif;font-size:14px;\">\r\n		<span></span><img src=\"http://img1.qunarzz.com/vc/e0/a0/0e/60524f3b6307433388bf2abe90.jpg_92.jpg\" style=\"display:inline;\" alt=\"\" data-lazy=\"http://img1.qunarzz.com/vc/e0/a0/0e/60524f3b6307433388bf2abe90.jpg_92.jpg\" data-ke-data-lazy=\"http://img1.qunarzz.com/vc/e0/a0/0e/60524f3b6307433388bf2abe90.jpg_92.jpg\" /><span></span><span style=\"color:#E53333;font-size:14px;\"></span> \r\n	</p>\r\n	<p style=\"color:#555555;font-family:arial, simsun, sans-serif;font-size:14px;\">\r\n		<span style=\"color:#E53333;font-size:14px;\"></span> \r\n	</p>\r\n	<div>\r\n		<br />\r\n		<table>\r\n			<tbody>\r\n				<tr>\r\n					<td valign=\"middle\">\r\n						<p>\r\n							<span style=\"font-family:\" font-size:10.5pt;color:#000000;\"=\"\"><span>■<span> </span></span></span><span style=\"font-family:\" font-size:10.5pt;color:#000000;\"=\"\">旅游费用包含</span><span style=\"font-family:\" font-size:10.5pt;font-weight:bold;color:#000000;\"=\"\"></span> \r\n						</p>\r\n					</td>\r\n				</tr>\r\n				<tr>\r\n					<td valign=\"middle\">\r\n						<p>\r\n							<span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\">1、【交通】海南当地VIP空调旅游车，1人1正座（26座以下无行李箱）；</span><span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\">北京-三亚往返机票（含税、不含航空保险）；</span><span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\"></span> \r\n						</p>\r\n					</td>\r\n				</tr>\r\n				<tr>\r\n					<td valign=\"middle\">\r\n						<p>\r\n							<span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\">2、【住宿】全程入住海南指定特色酒店；</span><span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\"></span> \r\n						</p>\r\n					</td>\r\n				</tr>\r\n				<tr>\r\n					<td valign=\"middle\">\r\n						<p>\r\n							<span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\">3、【用餐】6正4早，</span><span style=\"font-family:\" font-size:9pt;\"=\"\">早餐五点一粥（房含早，早餐不用费用不退）；正餐<span>20</span><span>元</span><span>/</span><span>人，十菜一汤，十人一桌；人数不足</span><span>10</span><span>人，按人数配菜；</span></span><span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\"></span> \r\n						</p>\r\n					</td>\r\n				</tr>\r\n				<tr>\r\n					<td valign=\"middle\">\r\n						<p>\r\n							<span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\">4、【门票】景区首道门票；</span><span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\"></span> \r\n						</p>\r\n					</td>\r\n				</tr>\r\n				<tr>\r\n					<td valign=\"middle\">\r\n						<p>\r\n							<span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\">5、【儿童】1.2m以下儿童游客团费含车位正座及半价儿童餐；</span><span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\"></span> \r\n						</p>\r\n					</td>\r\n				</tr>\r\n				<tr>\r\n					<td valign=\"middle\">\r\n						<p>\r\n							<span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\">6、【保险】最高保额50万元/人海南旅行社责任险；</span><span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\"></span> \r\n						</p>\r\n					</td>\r\n				</tr>\r\n				<tr>\r\n					<td valign=\"middle\">\r\n						<p>\r\n							<span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\">7、【导游】中文导游服务。</span><span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\"></span> \r\n						</p>\r\n					</td>\r\n				</tr>\r\n				<tr>\r\n					<td valign=\"middle\">\r\n						<p>\r\n							<span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\">8、【基金】政府征收调节基金。</span><span style=\"font-family:\" font-size:9pt;color:#000000;\"=\"\"></span> \r\n						</p>\r\n					</td>\r\n				</tr>\r\n			</tbody>\r\n		</table>\r\n	</div>\r\n	<p style=\"color:#555555;font-family:arial, simsun, sans-serif;font-size:14px;\">\r\n		<span style=\"color:#666666;font-size:14px;\"></span> \r\n	</p>\r\n</div>\r\n<h3>\r\n	费用不包含\r\n</h3>\r\n<p>\r\n	<span style=\"font-family:宋体;font-size:9pt;\"><span><img src=\"http://img1.qunarzz.com/vc/85/65/e3/405a666beb74288c52154ed359.jpg_92.jpg\" style=\"display:inline;\" alt=\"\" data-lazy=\"http://img1.qunarzz.com/vc/85/65/e3/405a666beb74288c52154ed359.jpg_92.jpg\" data-ke-data-lazy=\"http://img1.qunarzz.com/vc/85/65/e3/405a666beb74288c52154ed359.jpg_92.jpg\" /></span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"border:3pt solid #FFFFFF;\" valign=\"center\">\r\n				<p style=\"text-align:left;\">\r\n					<span style=\"color:#000000;font-family:\" font-size:10.5pt;\"=\"\"><span>■<span> </span></span></span><span style=\"color:#000000;font-family:\" font-size:10.5pt;\"=\"\">旅游费用不含</span><span style=\"color:#000000;font-family:\" font-size:10.5pt;\"=\"\"></span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td valign=\"center\">\r\n				<p style=\"text-align:left;\">\r\n					<span style=\"color:#000000;font-family:\" font-size:9pt;\"=\"\">1、自由活动期间交通费和餐费；</span><span style=\"color:#000000;font-family:\" font-size:9pt;\"=\"\"></span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td valign=\"center\">\r\n				<p style=\"text-align:left;\">\r\n					<span style=\"color:#000000;font-family:\" font-size:9pt;\"=\"\">2、全程入住酒店产生的单房差及加床费用；</span><span style=\"color:#000000;font-family:\" font-size:9pt;\"=\"\"></span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td valign=\"center\">\r\n				<p style=\"text-align:left;\">\r\n					<span style=\"color:#000000;font-family:\" font-size:9pt;\"=\"\">3、旅游意外保险及航空保险（建议旅游者购买）；</span><span style=\"color:#000000;font-family:\" font-size:9pt;\"=\"\"></span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td valign=\"center\">\r\n				<p style=\"text-align:left;\">\r\n					<span style=\"color:#000000;font-family:\" font-size:9pt;\"=\"\">4、因交通延误、取消等意外事件或不可抗力原因导致的额外费用；</span><span style=\"color:#000000;font-family:\" font-size:9pt;\"=\"\"></span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td valign=\"center\">\r\n				<p style=\"text-align:left;\">\r\n					<span style=\"color:#000000;font-family:\" font-size:9pt;\"=\"\">5、儿童报价以外产生的其他费用需游客自理；</span><span style=\"color:#000000;font-family:\" font-size:9pt;\"=\"\"></span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td valign=\"center\">\r\n				<p style=\"text-align:left;\">\r\n					<span style=\"color:#000000;font-family:\" font-size:9pt;\"=\"\">6、因旅游者违约、自身过错、自身疾病等自身原因导致的人身财产损失而额外支付的费用失而额外支付的费用；</span><span style=\"color:#000000;font-family:\" font-size:9pt;\"=\"\"></span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td valign=\"center\">\r\n				<p style=\"text-align:left;\">\r\n					<span style=\"color:#000000;font-family:\" font-size:9pt;\"=\"\">7、不占床位游客不含早餐。</span><span style=\"color:#000000;font-family:\" font-size:9pt;\"=\"\"></span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td valign=\"center\">\r\n				<p style=\"text-align:left;\">\r\n					<span style=\"font-family:\" font-size:9pt;\"=\"\">8、“</span><span style=\"color:#000000;font-family:\" font-size:9pt;\"=\"\">旅游费用包含”内容以外的所有费用；</span> \r\n				</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', ' 12月09日、12月10日、12月11日等 ', '1599', '飞机去 11:00 飞机回 17:15', '三亚', '2899', '5天', '/lvyou/upload/temp/db660df687041e8a213a9cdb.jpg_r_390x260x90_2c9f81f7.jpg', '杭州出发三亚自由行 5天4晚 机票+接机');
INSERT INTO `xianlu` VALUES ('3', '宁波 出发 ', '<div id=\"js_payment\">\r\n	<div class=\"det_l_content\">\r\n		<div class=\"payment\">\r\n			<div class=\"block\">\r\n				<h3>\r\n					费用包含\r\n				</h3>\r\n				<div class=\"cf\">\r\n					<p data-oritext=\"1、全程赠送一天自驾--丰田花冠1.6自动挡2014年新车\">\r\n						<strong><span style=\"font-size:18px;color:#337FE5;\"><span style=\"font-size:16px;\"><img src=\"http://img1.qunarzz.com/vc/cb/88/04/0e76008c83d0e382f2363d67ef.jpg_92.jpg\" style=\"display:inline;\" data-lazy=\"http://img1.qunarzz.com/vc/cb/88/04/0e76008c83d0e382f2363d67ef.jpg_92.jpg\" data-ke-data-lazy=\"http://img1.qunarzz.com/vc/cb/88/04/0e76008c83d0e382f2363d67ef.jpg_92.jpg\" alt=\"\" /><br />\r\n</span></span></strong> \r\n					</p>\r\n					<p data-oritext=\"1、全程赠送一天自驾--丰田花冠1.6自动挡2014年新车\">\r\n						<strong><span style=\"font-size:18px;color:#337FE5;\"><span style=\"font-size:16px;\">住宿：</span> </span></strong> \r\n					</p>\r\n					<p data-oritext=\"1、全程赠送一天自驾--丰田花冠1.6自动挡2014年新车\">\r\n						<strong><span style=\"color:#EE33EE;\"><span style=\"font-size:14px;color:#337FE5;\">三亚湾红树林木棉度假酒店 </span><span style=\"font-size:14px;color:#337FE5;\">高级房2晚</span></span></strong> \r\n					</p>\r\n					<p>\r\n						<strong><strong><span style=\"color:#EE33EE;\"><span style=\"font-size:14px;color:#337FE5;\">海棠湾香格里拉大酒店 </span><strong><strong><span style=\"color:#337FE5;font-size:14px;\">高级房2晚</span></strong></strong></span></strong></strong> \r\n					</p>\r\n					<p>\r\n						<strong><span style=\"color:#337FE5;font-size:16px;\"></span></strong> \r\n					</p>\r\n					<p style=\"color:#555555;font-family:arial, simsun, sans-serif;font-size:14px;\">\r\n						<strong><strong><span style=\"font-family:宋体;color:#CC33E5;font-size:16px;\"><span style=\"font-size:18px;\"><span style=\"font-size:14px;color:#337FE5;\">餐饮</span><strong><span style=\"font-size:14px;color:#337FE5;\">：</span></strong></span><span style=\"font-size:14px;color:#337FE5;\">含三亚湾红树林价值598RMB双人早餐、海棠湾香格里拉酒店价值698RMB双人早餐</span></span></strong></strong> \r\n					</p>\r\n					<p style=\"color:#555555;font-family:arial, simsun, sans-serif;font-size:14px;\">\r\n						<strong><span style=\"font-family:宋体;color:#CC33E5;\"><span style=\"font-family:微软雅黑, sans-serif;\"><span style=\"font-size:14px;color:#337FE5;\"><strong>交通</strong></span><span style=\"font-family:宋体;font-size:18px;\"><strong><span style=\"font-size:14px;color:#337FE5;\">：赠送往返三趟接送机服务，享受完美旅程</span></strong></span></span></span></strong> \r\n					</p>\r\n					<p style=\"color:#555555;font-family:arial, simsun, sans-serif;font-size:14px;\">\r\n						<strong><span style=\"font-family:宋体;color:#CC33E5;\"><span style=\"font-family:微软雅黑, sans-serif;\"><span style=\"font-family:宋体;font-size:18px;\"><strong><span style=\"font-size:14px;color:#337FE5;\">其他：特别赠送分界洲岛+奥特莱斯一日游</span></strong></span></span></span></strong> \r\n					</p>\r\n					<p style=\"color:#555555;font-family:arial, simsun, sans-serif;font-size:14px;\">\r\n						<strong><span style=\"font-family:宋体;color:#CC33E5;\"><span style=\"font-family:微软雅黑, sans-serif;\"><span style=\"font-family:宋体;font-size:18px;\"><strong><span style=\"font-size:14px;color:#337FE5;\"><span><span><span><span><span>请携带好本人身份证，儿童携带户口本或者出生证明提前两小时到达宁波栎社机场办理登机手续！</span><br />\r\n<span>宁波-三亚往返机票，参考航班：</span><br />\r\n<span>奥凯航空：去BK2835(15:10--18:00) </span><br />\r\n<span> 回BK2836(19:05--21:30) </span><br />\r\n<span>四川航空：去3U8766(19:45--22:45) </span><br />\r\n<span> 回3U8765(10:05--12:35)</span><br />\r\n</span></span></span></span><span>（报价均按照价格最低的航班，如该航班机位临时售完，或您对航班有要求，可能需要补相应的差价，烦请谅</span><span>解。）</span><br />\r\n</span></strong></span></span></span></strong><strong><span style=\"color:#337FE5;\">特别说明：本价格不适用，如遇法定节假日请咨询客服，具体价格以客服给出价格为主，谢谢理解，祝{旅行愉快}。客服电话：025-66600305</span></strong> \r\n					</p>\r\n				</div>\r\n			</div>\r\n			<div class=\"block\">\r\n				<h3>\r\n					费用不包含\r\n				</h3>\r\n				<div class=\"cf\">\r\n					<p data-oritext=\"1、单房差：单人入住须补房差\">\r\n						1、单房差：单人入住须补房差\r\n					</p>\r\n					<p data-oritext=\"2、保险：不含旅游人身意外保险, 建议您自行购买\">\r\n						2、保险：不含旅游人身意外保险, 建议您自行购买\r\n					</p>\r\n					<p data-oritext=\"3、升级舱位、升级酒店、升级房型等产生的差价\">\r\n						3、升级舱位、升级酒店、升级房型等产生的差价\r\n					</p>\r\n					<p data-oritext=\"4、当地政府调节金：依照有关部门的相关规定收取\">\r\n						4、当地政府调节金：依照有关部门的相关规定收取\r\n					</p>\r\n					<p data-oritext=\"5、因交通延阻、罢工、天气、飞机机器故障、航班取消或更改时间其它不可抗力原因导致的费用 \">\r\n						5、因交通延阻、罢工、天气、飞机机器故障、航班取消或更改时间其它不可抗力原因导致的费用\r\n					</p>\r\n					<p data-oritext=\"6、个人消费：酒店内洗衣、理发、电话、传真、收费电视、饮品、烟酒等个人消费产生的费用 \">\r\n						6、个人消费：酒店内洗衣、理发、电话、传真、收费电视、饮品、烟酒等个人消费产生的费用\r\n					</p>\r\n				</div>\r\n			</div>\r\n			<div class=\"block \" id=\"js_fee_desc\">\r\n				<h3>\r\n					费用说明\r\n				</h3>\r\n				<div placeholder=\"content\">\r\n					单人预定需补齐单房差\r\n				</div>\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>\r\n<div class=\"bluetitle\">\r\n	<span class=\"narrow\"></span><b>线路特色</b> \r\n</div>\r\n<p style=\"color:#555555;font-family:arial, simsun, sans-serif;font-size:14px;\">\r\n	<b><span style=\"font-size:16pt;font-family:宋体;color:#E53333;\">产品特色推荐理由：</span></b> \r\n</p>\r\n<p style=\"color:#555555;font-family:arial, simsun, sans-serif;font-size:14px;\">\r\n	<strong><span style=\"font-family:宋体;\"><span style=\"color:#4C33E5;\">★</span><span style=\"color:#4C33E5;\">2晚三亚湾红树林木棉度假酒店 &nbsp;&nbsp;高级房 &nbsp; 2晚 </span></span></strong> \r\n</p>\r\n<p style=\"color:#555555;font-family:arial, simsun, sans-serif;font-size:14px;\">\r\n	<strong><span style=\"font-family:宋体;\"><span style=\"color:#4C33E5;\"><strong>★2晚海棠湾香格里拉度假酒店 &nbsp; &nbsp;&nbsp;高级房 </strong><span style=\"color:#555555;\"><span style=\"color:#4C33E5;\"><strong>2晚</strong></span></span></span></span></strong> \r\n</p>\r\n<p style=\"color:#555555;font-family:arial, simsun, sans-serif;font-size:14px;\">\r\n	<b><span style=\"font-family:宋体;\"><strong><span style=\"color:#4C33E5;\">入住介绍：</span></strong></span></b> \r\n</p>\r\n<p style=\"color:#555555;font-family:arial, simsun, sans-serif;font-size:14px;\">\r\n	<span style=\"color:#4C33E5;